export function showHome(request, reply) {
  const username = request.auth.credentials.id
  reply(`
    <!DOCTYPE html>
    <html lang="en">
      <head>
        <meta charset="utf-8"/>
        <meta name="viewport" content="width=device-width, initial-scale=1"/>
        <meta http-equiv="x-ua-compatible" content="ie=edge"/>
        <title>Admin</title>
        <link type="text/css" rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.0.0-alpha.2/css/bootstrap.min.css"/>
      </head>
      <body>
        <div class="container">
          <h1>Admin</h1>
          <h2>${username} <a href="/logout">Log out</a></h2>
          <h3>Blog</h3>
          <ul>
            <li>
              <a href="/blog/update">Update blog content</a>
            </li>
          </ul>
        </div>
      </body>
    </html>
  `)
}
