import path from "path"
import routes from "app/routes"

export function register(server, options, next) {
  server.register([
    require("inert")
  ], error => {
    if (error) {
      throw error
    }
  })
  server.bind({
    options
  })
  server.route(routes)
  next()
}

register.attributes = {
  pkg: require(path.join(__dirname, "../../../package.json"))
}
