import path from "path"
import routes from "app/routes"

export function register(server, options, next) {
  server.register([
    require("inert")
  ], next)
  server.path(path.join(__dirname, "../../../public"))
  server.bind({
    options
  })
  server.route(routes)
  next()
}

register.attributes = {
  pkg: require(path.join(__dirname, "../../../package.json"))
}
