import {login, logout} from "app/handlers/auth"
import {showHome} from "app/handlers/home"
import {updateBlog} from "app/handlers/blog"

const noAuthRequired = {
  plugins: {
    "hapi-auth-cookie": {
      redirectTo: false
    }
  }
}

const routes = [
  {
    method: "GET",
    path: "/robots.txt",
    handler: {
      file: "robots.txt"
    },
    config: noAuthRequired
  },
  {
    method: ["GET", "POST"],
    path: "/login",
    handler: login,
    config: noAuthRequired
  },
  {
    method: "GET",
    path: "/logout",
    handler: logout,
    config: noAuthRequired
  },
  {
    method: "GET",
    path: "/",
    handler: showHome
  },
  {
    method: "GET",
    path: "/blog/update",
    handler: updateBlog
  }
]

export default routes
