import path from "path"
import {showHome} from "app/handlers/home"
import {updateBlog} from "app/handlers/blog"

const publicPath = path.join(__dirname, "../../../public")

const routes = [
  {
    method: "GET",
    path: "/robots.txt",
    handler: {
      file: path.join(publicPath, "robots.txt")
    }
  },
  {
    method: "GET",
    path: "/",
    handler: showHome
  },
  {
    method: "GET",
    path: "/blog/update",
    handler: updateBlog
  }
]

export default routes
